import React, { Component } from 'react';
import Habit from './habit';

class Habits extends Component {
  // 숫자를 증가하고 감소한다는 것은 어떤 특정한 habit의 count를 증가하고 감소하는 거기 때문에
  //인자로는 어떤 특정한 habit을 전달받으면 그 habit에 해당하는 count를 증가해주면 되겠죠.
  handleIncrement = (habit) => {
    this.props.onIncrement(habit);
  };

  handleDecrement = (habit) => {
    this.props.onDecrement(habit);
  };

  handleDelete = (habit) => {
    console.log(this.props.habit.id);
    console.log(habit.id);
    const habits = this.state.habits.filter((item) => item.id !== habit.id); //item.id와 habit.id의 값이 다를때 배열을 남기고 새로운 배열로 반환
    this.setState({ habits });
  };

  render() {
    return (
      <ul>
        {
          //this.state.habits.map(arrayItem => (
          //  <Habit propNameHabit={arrayItem}/>
          <Habit
            key={this.props.habit.id}
            habit={this.props.habit}
            onIncrement={this.handleIncrement} //habit.jsx에서 '+'를 클릭하면 onClick이 this.props.onIncrement(this.props.habit)를 전달
            onDecrement={this.handleDecrement}
            onDelete={this.handleDelete}
          />
        }
      </ul>
    );
  }
}

export default Habits;
