import './app.css';
import React, { Component } from 'react';
import Habits from './components/habits';
//이 habits라는 컴포넌트 안에 전체적인 데이터가 들어있기 때문에 데이터가 삭제되거나 추가되거나 변경되는 경우,
// 이 컴포넌트 안에서 수정하는 로직들을 가지고 있어야합니다.
// 한 마디로 데이터를 가지고 있는 곳이 내가 이 데이터를 어떻게 수정하면 되는지 잘 알고 있는 최고의 장소이기 때문에
// 여기에서 처리해주는 것이 가장 좋습니다.
class App extends Component {
  state = {
    habits: [
      { id: 1, name: 'Reading', count: 0 },
      { id: 2, name: 'Running', count: 0 },
      { id: 3, name: 'Coding', count: 0 },
    ],
    input: '',
  };

  handleChange = (event) => {
    this.setState({ input: event.target.value });
  };
  handleClick = () => {
    const habits = [...this.state.habits];
    const id = new Date().getTime();
    habits.push({ id: id, name: this.state.input, count: 0 });
    this.setState({ habits });
    console.log(this.state.input);
    console.log(habits);
  };
  addCount = () => {};
  render() {
    return (
      <div>
        <Navber />
        <div>
          <input type='text' placeholder='Habit' onChange={this.handleChange} />
          <button onClick={this.handleClick}>Add</button>
        </div>
        <section>
          {this.state.habits.map((habit) => (
            <Habits key={habit.id} habit={habit} />
          ))}
        </section>
        <button>Reset All</button>
      </div>
    );
  }
}
export default App;
